import React, {useState} from 'react'
import { FaSadCry } from 'react-icons/fa';

function Dropdown({ title, items = [], multiSelect = false }) {
    const [open, setOpen] = useState(false);
    const [selection, setSelection] = useState([]);
    const toggle = () => setOpen(!open);

    const handleOnClick = (item) => {
        if (!selection.some(current => current.id == item.id)) {
            if (!multiSelect) {
                setSelection([item]);
            } else if (multiSelect) {
                setSelection([...selection, item])
            }
        } else {
            let selectionRemoval = selection;
            selectionRemoval = selectionRemoval.filter(
                current => current.id == item.id
            );

            setSelection([...selectionRemoval]);
        }
    }

    const isItemInSelection = (item) => {
        if (selection.find(current => current.id == item.id)) {
            return true;
        }
        return false;
    }

    return (
        <div className="dd-wrapper">
            <div
                tabIndex={0}
                className="dd-header"
                role="button"
                onKeyPress={() => toggle(!open)}
                onClick={() => toggle(!open)}
            >
                <div className="dd-header_title">
                    <p className="dd-header_title--bold">{title}</p>
                </div>
                <div className="dd-header_action">
                    <p>{open ? 'Close' : 'Open'}</p>
                </div>
            </div>
            {open && (
                <ul className="dd-list">
                    {items.map(item => (
                        <li className="dd-list-item" key={item.id}>
                            <button type="button" onClick={() => handleOnClick(item)}>
                                <span>{item.value}</span>
                                <span>Selected...</span>
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
        // <select name="selectPlatform" id="selectPlatform">
        //     <option value="psn">PlayStation</option>
        //     <option value="xbl">Xbox</option>
        //     <option value="battle">Battle.net</option>
        //     <option value="acti">Activision ID</option>
        // </select>
    )
}

export default Dropdown
